// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

library StringUtils {
  function equal(string memory a, string memory b) internal pure returns (bool) {
    return (keccak256(abi.encodePacked((a))) == keccak256(abi.encodePacked((b))));
  }

  function isEmpty(string memory a) internal pure returns (bool) {
    return (bytes(a).length == 0);
  }
}

contract Auth {
  using StringUtils for string;

  struct User {
    bytes32 hashedPassword;
    bool registered;
    bytes32 hashedEmail;
  }

  mapping(address => User) private users;

  event UserRegistered(address indexed userAddress);

  modifier onlyRegistered() {
    require(users[msg.sender].registered, "User not registered");
    _;
  }

  modifier onlyNotRegistered() {
    require(!users[msg.sender].registered, "User already registered");
    _;
  }

  function register(string memory username, string memory email, string memory password) public onlyNotRegistered
  {
    require(!username.isEmpty(), "Username cannot be empty");
    require(!email.isEmpty(), "Email cannot be empty");
    require(!password.isEmpty(), "Password cannot be empty");
    require(bytes(password).length >= 8, "Password must be at least 8 characters long");

    bytes32 hashedPassword = keccak256(abi.encodePacked(password));
    bytes32 hashedEmail = keccak256(abi.encodePacked(email));
    users[msg.sender] = User(hashedPassword, true, hashedEmail);

    emit UserRegistered(msg.sender);
  }

  function verify(string memory email, string memory password) public view onlyRegistered returns (bool) {
  bytes32 hashedPassword = keccak256(abi.encodePacked(password));
  bytes32 hashedEmail = keccak256(abi.encodePacked(email));
  return hashedPassword == users[msg.sender].hashedPassword && hashedEmail == users[msg.sender].hashedEmail;
}

}
